set-header.value:
@{
var principal = new
{
  auth_typ = "Bearer",
  name_typ = "name",
  role_typ = "role",
  claims = new[]
  {
    new
    {
      typ = "sub",
      val = "123",
    },
    new
    {
      typ = "name",
      val = "Test User",
    },
    new
    {
      typ = "role",
      val = "admin",
    },
  },
};
var json =  JsonConvert.SerializeObject(principal);
var base64 = Convert.ToBase64String(Encoding.UTF8.GetBytes(json));

return base64;
      }
